import React from 'react'
import { Link } from 'react-router-dom'
import Flip from 'react-reveal/Flip'
import Fade from 'react-reveal/Fade'

import '../Assets/CSS/Header.css'

import LogoOutlined from '../Assets/Images/logo-1.png'

class Header extends React.Component{

    constructor(){
        super()
        this.state = {
            scroll: false,
            openMenu: false
        }

        this.openMenu = this.openMenu.bind(this);
    }

    componentDidMount(){
        window.addEventListener('scroll', this.scrolled)
    }

    scrolled = () => {
        if (window.scrollY >= 120) {
            this.setState({
                scroll: true
            });
        }else{
            this.setState({
                scroll: false
            });
        }    
    }

    openMenu = (e) => {
        e.preventDefault()
        this.setState({
            openMenu: true
        });

        console.log(this.state.openMenu)
    }

    render(){

        const navbarScroll = {
            borderBottom: "",
            transition: "1.2s"
        }

        const liScroll = {
            color: ""
        }

        const menuStyle = {
            width : "0%"
        }

        if(this.state.scroll){
            navbarScroll.backgroundColor= "white"
            liScroll.color= "#CD2029" 
            navbarScroll.borderBottom = "1px solid #CD2029"
        }
        else{
            navbarScroll.borderBottom=""
        }

        if(this.state.openMenu){
            menuStyle.width= "90%"
        }

        return(    
            <header className="navbar" style={navbarScroll}>
                <ul>
                    <li className="desktop-mobile">
                        <span>
                            <Link to = "/" className="linkStyle" >
                                <Flip left>        
                                    <img src= { LogoOutlined } className="small-img-width" alt="LOGO" align="left" />
                                </Flip>
                            </Link>
                        </span>                            
                    </li>
                    <li style={liScroll} className="desktop">
                        <Link to = "/" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect">
                                    Beranda 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll} className="desktop">
                        <Link to = "/About" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Tentang Kami 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll} className="desktop">
                        <Link to = "/Product" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Jasa Kami 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll} className="desktop">
                        <Link to = "/Contact" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Kontak 
                                </b>
                            </Fade>
                        </Link>
                    </li>

                    <li style={menuStyle} className="mobile" onClick={this.openMenu} >
                        <span className="menu-size">
                            &#9776;
                        </span>
                    </li>

                </ul>


    <div class="menu-container" id="navbar">
        s
    </div>

            </header>
        )
    }
}

export default Header
