import React from 'react'
import { Link } from 'react-router-dom'
import Flip from 'react-reveal/Flip'
import Fade from 'react-reveal/Fade'

import '../Assets/CSS/Header.css'

import LogoOutlined from '../Assets/Images/logo-outlined.png'

class Header2 extends React.Component{

    constructor(){
        super()
        this.state = {
            scroll: false
        }

        this.scrolled = this.scrolled.bind(this);
    }

    componentDidMount(){
        window.addEventListener('scroll', this.scrolled)
    }

    scrolled = () => {
        if (window.scrollY >= 120) {
            this.setState({
                scroll: true
            });
        }else{
            this.setState({
                scroll: false
            });
        }    
    }

    render(){

        const navbarScroll = {
            borderBottom: "",
            transition: "1.2s",
            backgroundColor: "#CD2029"
        }

        const liScroll = {
            color: ""
        }

        if(this.state.scroll){
            navbarScroll.backgroundColor= "white"
            liScroll.color= "#CD2029" 
            navbarScroll.borderBottom = "1px solid #CD2029"
        }
        else{
            navbarScroll.borderBottom=""
        }

        return(    
            <header className="navbar" style={navbarScroll}>
                <ul>
                    <li>   
                        <span>
                            <Link to = "/" className="linkStyle" >
                                <Flip left>        
                                    <img src= { LogoOutlined } className="small-img-width" alt="LOGO" align="left" />
                                </Flip>
                            </Link>
                        </span>                            
                    </li>
                    <li style={liScroll}>
                        <Link to = "/" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect">
                                    Beranda 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll}>
                        <Link to = "/About" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Tentang Kami 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll}>
                        <Link to = "/Product" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Jasa Kami 
                                </b>
                            </Fade>
                        </Link>
                    </li>
                    <li style={liScroll}>
                        <Link to = "/Contact" className="linkStyle" >
                            <Fade bottom>
                                <b className="underlined-effect"> 
                                    Kontak 
                                </b>
                            </Fade>
                        </Link>
                    </li>

                    <li className="shortcut-menu">
                        <span className="s-menu">
                            <div className="s-menu1"></div>
                            <div className="s-menu2"></div>
                            <div className="s-menu3"></div>
                            <div className="s-menu2"></div>
                        </span>
                    </li>
                </ul>
            </header>
        )
    }
}

export default Header2
